import { 
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LGOIN_FAIL
 } from '../constants/userConstants'

export const userLogin = (state, action) => {
    switch(action.payload){
        case USER_LOGIN_REQUEST:
            return {loading: true, }

        case USER_LOGIN_SUCCESS:
            return {loading: false,}

        case USER_LGOIN_FAIL:
            return {loading: false,}

        default:
            return state

    }
}
