import React,{ useState, useEffect } from 'react'
import { Link, useLocation, useNavigate } from 'react-router-dom'
import { Form, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import FormContainer from '../components/FormContainer'
import { login } from '../actions/userActions'
import Message from '../components/Message'
import Loader from '../components/Loader'

function LoginScreen() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    //Warning: A component is changing an uncontrolled input to be controlled. This is likely caused
    // useState 에 '' 안 넣으니까 이 뜸

    // 500 erro 는 / 를 안넣어서, 401 error 는 django signal 때문에

    const { search } = useLocation();
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const redirect = search ? search.split('=')[1] : '/'

    const userLogin = useSelector(state => state.userLogin)
    // useSelector 이새끼가 ㅋㅋ store.js 에서 reducer 가져오는 새끼임
    const {loading, error, userInfo} = userLogin


    useEffect(() => {
        if(userInfo){
            navigate(redirect)
        }
    },[userInfo, redirect])


    const submitHandler = (e) => {
        e.preventDefault()
        dispatch(login(email, password))

    }




    return (
        <FormContainer>
            <h1>Sign In</h1>
            {error && <Message variant='danger'>{error}</Message>}
            {loading && <Loader />}
            <Form onSubmit={submitHandler}>

                <Form.Group controlId='email'>
                    <Form.Label>Email Address</Form.Label>
                    <Form.Control
                        type='email'
                        placeholder='Enter Email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>


                <Form.Group controlId='password'>
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type='password'
                        placeholder='Enter Password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>

                <Button type='submit' variant='primary'>
                    Sign In
                </Button>
            </Form>

            <Row className='py-3'>
                <Col>
                    New Customer? <Link
                        to={redirect ? `/register?redirect=${redirect}` : '/register'}>
                        Register
                        </Link>
                </Col>
            </Row>

        </FormContainer>
    )
}

export default LoginScreen
